// cypress/e2e/user-login.cy.js
// Basic Cypress E2E test for user login flow

describe('User Authentication', () => {
  beforeEach(() => {
    // Visit the application before each test
    cy.visit('/login');
  });

  it('should successfully log in with valid credentials', () => {
    // Enter username
    cy.get('[data-testid="username-input"]')
      .type('john.doe@example.com');

    // Enter password
    cy.get('[data-testid="password-input"]')
      .type('securePassword123');

    // Submit the form
    cy.get('[data-testid="login-button"]')
      .click();

    // Verify successful login
    cy.url().should('include', '/dashboard');
    cy.get('[data-testid="user-menu"]')
      .should('contain', 'Welcome, John');

    // Check that auth token is stored
    cy.window()
      .its('localStorage')
      .invoke('getItem', 'authToken')
      .should('exist');
  });

  it('should show error message for invalid credentials', () => {
    cy.get('[data-testid="username-input"]')
      .type('invalid@example.com');

    cy.get('[data-testid="password-input"]')
      .type('wrongpassword');

    cy.get('[data-testid="login-button"]')
      .click();

    // Should remain on login page
    cy.url().should('include', '/login');

    // Error message should appear
    cy.get('[data-testid="error-message"]')
      .should('be.visible')
      .and('contain', 'Invalid credentials');
  });

  it('should handle empty form submission', () => {
    cy.get('[data-testid="login-button"]')
      .click();

    // Validation errors should appear
    cy.get('[data-testid="username-error"]')
      .should('contain', 'Username is required');

    cy.get('[data-testid="password-error"]')
      .should('contain', 'Password is required');
  });
});

