// Playwright E2E test for e-commerce shopping cart

const { test, expect } = require('@playwright/test');

test.describe('Shopping Cart Functionality', () => {
  test.beforeEach(async ({ page }) => {
    // Navigate to the products page
    await page.goto('/products');

    // Wait for products to load
    await page.waitForSelector('[data-testid="product-grid"]');
  });

  test('should add product to cart and proceed to checkout', async ({ page }) => {
    // Add first product to cart
    await page.click('[data-testid="product-1"] [data-testid="add-to-cart"]');

    // Verify cart badge updates
    await expect(page.locator('[data-testid="cart-badge"]')).toContainText('1');

    // Open cart
    await page.click('[data-testid="cart-icon"]');

    // Verify product in cart
    await expect(page.locator('[data-testid="cart-item"]')).toBeVisible();
    await expect(page.locator('[data-testid="cart-total"]')).toContainText('$29.99');

    // Proceed to checkout
    await page.click('[data-testid="checkout-button"]');

    // Verify on checkout page
    await expect(page).toHaveURL(/.*checkout/);
    await expect(page.locator('h1')).toContainText('Checkout');
  });

  test('should handle empty cart scenario', async ({ page }) => {
    // Go directly to cart
    await page.click('[data-testid="cart-icon"]');

    // Verify empty cart message
    await expect(page.locator('[data-testid="empty-cart-message"]'))
      .toContainText('Your cart is empty');

    // Checkout button should be disabled
    await expect(page.locator('[data-testid="checkout-button"]'))
      .toBeDisabled();
  });

  test('should work across different browsers', async ({ browserName, page }) => {
    console.log(`Running on ${browserName}`);

    // Test core functionality
    await page.click('[data-testid="product-1"] [data-testid="add-to-cart"]');
    await expect(page.locator('[data-testid="cart-badge"]')).toContainText('1');

    // Browser-specific behavior could be tested here
    if (browserName === 'webkit') {
      // Safari-specific tests
    }
  });

  test('should handle network failures gracefully', async ({ page }) => {
    // Intercept and block network request
    await page.route('**/api/cart', route => {
      route.abort();
    });

    // Try to add to cart
    await page.click('[data-testid="product-1"] [data-testid="add-to-cart"]');

    // Verify error handling
    await expect(page.locator('[data-testid="error-toast"]'))
      .toContainText('Failed to add item to cart');
  });
});

