describe('E-Commerce Application Tests', () => {
  beforeEach(() => {
    // Reset database and seed test data before each test
    cy.task('seedDatabase');
    cy.visit('/');
  });

  afterEach(() => {
    // Take screenshot on test failure for debugging
    if (Cypress.currentTest.state === 'failed') {
      cy.screenshot(`failure-${Cypress.currentTest.title}`);
    }
  });

  it('allows users to complete a purchase flow', () => {
    // Using custom commands for common actions
    cy.login('user@example.com', 'password123');

    // Navigate to products page
    cy.get('[data-cy=nav-products]').click();

    // Search for a product
    cy.searchProduct('laptop');

    // Add product to cart
    cy.get('[data-cy=product-item]').first().within(() => {
      cy.get('[data-cy=add-to-cart]').click();
    });

    // Go to cart
    cy.get('[data-cy=cart-link]').click();

    // Verify cart contents
    cy.get('[data-cy=cart-items]').should('have.length', 1);
    cy.get('[data-cy=cart-total]').should('contain', '$999.99');

    // Proceed to checkout
    cy.get('[data-cy=checkout-button]').click();

    // Fill shipping information using page object
    const checkoutPage = new CheckoutPage();
    checkoutPage.fillShippingInfo({
      firstName: 'John',
      lastName: 'Doe',
      address: '123 Main St',
      city: 'New York',
      zipCode: '10001'
    });

    // Confirm order
    cy.get('[data-cy=order-summary]').should('be.visible');
    cy.get('[data-cy=confirm-order]').click();

    // Verify success message
    cy.get('[data-cy=order-success]').should('contain', 'Order placed successfully');
    cy.get('[data-cy=order-number]').should('contain', 'ORD-');
  });

  it('shows error for invalid coupon code', () => {
    cy.login('user@example.com', 'password123');

    // Add item to cart
    cy.get('[data-cy=nav-products]').click();
    cy.get('[data-cy=product-item]').first().within(() => {
      cy.get('[data-cy=add-to-cart]').click();
    });

    // Go to cart
    cy.get('[data-cy=cart-link]').click();

    // Apply invalid coupon using custom command
    cy.applyCoupon('INVALID');

    // Verify error message
    cy.get('[data-cy=coupon-error]').should('be.visible')
      .and('contain', 'Invalid coupon code');
  });

  it('handles network errors gracefully', () => {
    // Intercept API calls and simulate network failure
    cy.intercept('POST', '/api/checkout', {
      statusCode: 500,
      body: { error: 'Internal server error' }
    }).as('checkoutRequest');

    cy.login('user@example.com', 'password123');

    // Add item to cart and proceed to checkout
    cy.get('[data-cy=nav-products]').click();
    cy.get('[data-cy=product-item]').first().within(() => {
      cy.get('[data-cy=add-to-cart]').click();
    });
    cy.get('[data-cy=cart-link]').click();
    cy.get('[data-cy=checkout-button]').click();

    // Fill shipping information
    const checkoutPage = new CheckoutPage();
    checkoutPage.fillShippingInfo({
      firstName: 'John',
      lastName: 'Doe',
      address: '123 Main St',
      city: 'New York',
      zipCode: '10001'
    });

    // Confirm order (will fail due to network error)
    cy.get('[data-cy=confirm-order]').click();

    // Verify error handling
    cy.get('[data-cy=error-message]').should('be.visible')
      .and('contain', 'Unable to process your order');
  });
});

