describe('Performance Monitoring Tests', () => {
  it('measures page load performance', () => {
    const startTime = performance.now();

    cy.visit('/dashboard')
      .then(() => {
        const endTime = performance.now();
        const loadTime = endTime - startTime;

        // Assert page loads within acceptable time
        expect(loadTime).to.be.lessThan(3000); // 3 seconds

        // Log performance metrics
        cy.task('logPerformance', {
          testName: 'Dashboard Load Time',
          duration: loadTime,
          timestamp: new Date().toISOString()
        });
      });
  });

  it('measures API response times', () => {
    cy.intercept('GET', '/api/users').as('getUsers');

    cy.visit('/users');
    cy.wait('@getUsers').then((interception) => {
      const responseTime = interception.response.headers['response-time'];
      expect(responseTime).to.be.lessThan(500); // 500ms

      cy.task('logPerformance', {
        testName: 'Users API Response Time',
        duration: responseTime,
        timestamp: new Date().toISOString()
      });
    });
  });
});

