// 3. Resource Utilization Monitoring
describe('Resource Utilization Tests', () => {
  it('monitors memory usage during complex interactions', () => {
    // Visit a complex page
    cy.visit('/dashboard');

    // Perform memory-intensive actions
    cy.get('[data-cy=refresh-data]').click();
    cy.get('[data-cy=refresh-data]').click();
    cy.get('[data-cy=refresh-data]').click();

    // Check for memory leaks
    cy.window().then((win) => {
      const memoryUsage = win.performance.memory;
      const usedMemory = memoryUsage.usedJSHeapSize;
      const totalMemory = memoryUsage.jsHeapSizeLimit;

      // Log memory usage
      cy.task('logMemoryUsage', {
        used: usedMemory,
        total: totalMemory,
        percentage: (usedMemory / totalMemory) * 100
      });

      // Assert memory usage is reasonable
      expect(usedMemory).to.be.lessThan(totalMemory * 0.8); // Less than 80%
    });
  });

  it('checks for DOM node leaks', () => {
    cy.visit('/list');

    // Count DOM nodes before
    cy.document().then((doc) => {
      const initialNodeCount = doc.querySelectorAll('*').length;

      // Perform actions that might create node leaks
      cy.get('[data-cy=refresh]').click();
      cy.get('[data-cy=refresh]').click();

      // Count DOM nodes after
      cy.document().then((doc) => {
        const finalNodeCount = doc.querySelectorAll('*').length;

        // Assert no significant increase in DOM nodes
        expect(finalNodeCount - initialNodeCount).to.be.lessThan(100);
      });
    });
  });
});

