// Custom task implementation (cypress/plugins/index.js)
module.exports = (on, config) => {
  let performanceData = [];

  on('task', {
    logPerformance(data) {
      performanceData.push(data);
      console.log(`Performance: ${data.testName} took ${data.duration}ms`);
      return null;
    },

    logMemoryUsage(data) {
      console.log(`Memory Usage: ${data.percentage.toFixed(2)}%`);
      return null;
    },

    generatePerformanceReport() {
      // Generate performance report
      const report = {
        timestamp: new Date().toISOString(),
        tests: performanceData,
        summary: {
          averageDuration: performanceData.reduce((sum, test) => sum + test.duration, 0) / performanceData.length,
          slowestTest: performanceData.reduce((slowest, test) => test.duration > slowest.duration ? test : slowest, { duration: 0 }),
          fastestTest: performanceData.reduce((fastest, test) => test.duration < fastest.duration ? test : fastest, { duration: Infinity })
        }
      };

      // Save report to file
      const fs = require('fs');
      fs.writeFileSync('performance-report.json', JSON.stringify(report, null, 2));

      return report;
    }
  });

  // Generate report after all tests
  on('after:run', (results) => {
    if (results) {
      return cy.task('generatePerformanceReport');
    }
  });
};

