// Page objects centralize element selectors and actions for each page
class LoginPage {
  constructor() {
    this.url = '/login';
    this.elements = {
      emailInput: '[data-cy=email]',
      passwordInput: '[data-cy=password]',
      submitButton: '[data-cy=submit]',
      errorMessage: '[data-cy=error-message]',
      successMessage: '[data-cy=success-message]'
    };
  }

  visit() {
    cy.visit(this.url);
    return this;
  }

  fillEmail(email) {
    cy.get(this.elements.emailInput).type(email);
    return this;
  }

  fillPassword(password) {
    cy.get(this.elements.passwordInput).type(password);
    return this;
  }

  submit() {
    cy.get(this.elements.submitButton).click();
    return this;
  }

  login(email, password) {
    this.fillEmail(email)
        .fillPassword(password)
        .submit();
    return this;
  }

  assertErrorMessage(message) {
    cy.get(this.elements.errorMessage)
      .should('be.visible')
      .and('contain', message);
    return this;
  }

  assertSuccessMessage(message) {
    cy.get(this.elements.successMessage)
      .should('be.visible')
      .and('contain', message);
    return this;
  }
}

