// 2. Test Review Checklist Implementation
class E2ETestReviewer {
  static reviewTest(testCode) {
    const issues = [];

    // Check for proper test structure
    if (!this.hasProperStructure(testCode)) {
      issues.push({
        severity: 'high',
        message: 'Test lacks proper structure (describe/it blocks)',
        suggestion: 'Use consistent describe/it structure with clear naming'
      });
    }

    // Check for page object usage
    if (!this.usesPageObjects(testCode)) {
      issues.push({
        severity: 'medium',
        message: 'Direct element selectors found instead of page objects',
        suggestion: 'Implement page objects for better maintainability'
      });
    }

    // Check for proper assertions
    if (!this.hasMeaningfulAssertions(testCode)) {
      issues.push({
        severity: 'medium',
        message: 'Assertions are too generic or missing',
        suggestion: 'Add specific, meaningful assertions that verify business outcomes'
      });
    }

    // Check for test isolation
    if (!this.hasProperSetupTeardown(testCode)) {
      issues.push({
        severity: 'high',
        message: 'Missing proper setup/teardown for test isolation',
        suggestion: 'Implement beforeEach/afterEach for test state management'
      });
    }

    // Check for error handling
    if (!this.handlesErrors(testCode)) {
      issues.push({
        severity: 'medium',
        message: 'Error scenarios not adequately tested',
        suggestion: 'Add tests for error conditions and edge cases'
      });
    }

    return issues;
  }

  static hasProperStructure(testCode) {
    // Check for describe/it structure
    return /describe\s*\(/.test(testCode) && /it\s*\(/.test(testCode);
  }

  static usesPageObjects(testCode) {
    // Check if test uses page objects instead of direct selectors
    const directSelectors = /cy\.get\s*\(\s*['"]\.[^"']*['"]\s*\)/.test(testCode);
    const pageObjectUsage = /new [A-Z][a-zA-Z]*Page\s*\(/.test(testCode);
    return pageObjectUsage || !directSelectors;
  }

  static hasMeaningfulAssertions(testCode) {
    // Check for specific assertions rather than generic ones
    return /should\s*\(.*\)/.test(testCode) || /expect\s*\(.*\)\.to/.test(testCode);
  }

  static hasProperSetupTeardown(testCode) {
    return /beforeEach\s*\(/.test(testCode) || /afterEach\s*\(/.test(testCode);
  }

  static handlesErrors(testCode) {
    // Check for error scenario testing
    return /catch\s*\(/.test(testCode) || /should.*error/.test(testCode) || /should.*fail/.test(testCode);
  }
}

