// 2. Cypress Custom Task for Metrics Collection
module.exports = (on, config) => {
  let testMetrics = {
    totalTests: 0,
    passedTests: 0,
    failedTests: 0,
    totalDuration: 0,
    testDurations: []
  };

  on('task', {
    // Record test metrics
    recordTestResult({ testName, status, duration }) {
      testMetrics.totalTests++;

      if (status === 'passed') {
        testMetrics.passedTests++;
      } else if (status === 'failed') {
        testMetrics.failedTests++;
      }

      testMetrics.totalDuration += duration;
      testMetrics.testDurations.push({
        testName,
        duration,
        timestamp: new Date().toISOString()
      });

      // Log to console for immediate feedback
      console.log(`Test ${testName}: ${status} (${duration}ms)`);

      return null;
    },

    // Generate metrics report
    generateMetricsReport() {
      const report = {
        summary: {
          totalTests: testMetrics.totalTests,
          passedTests: testMetrics.passedTests,
          failedTests: testMetrics.failedTests,
          passRate: (testMetrics.passedTests / testMetrics.totalTests * 100).toFixed(2) + '%',
          averageDuration: (testMetrics.totalDuration / testMetrics.totalTests).toFixed(2) + 'ms',
          timestamp: new Date().toISOString()
        },
        slowestTests: testMetrics.testDurations
          .sort((a, b) => b.duration - a.duration)
          .slice(0, 5)
      };

      // Save to file
      const fs = require('fs');
      fs.writeFileSync('test-metrics-report.json', JSON.stringify(report, null, 2));

      // Log summary
      console.log(`Test Metrics Report: ${report.summary.passRate} pass rate, ${report.summary.averageDuration} average duration`);

      return report;
    },

    // Reset metrics between test runs
    resetMetrics() {
      testMetrics = {
        totalTests: 0,
        passedTests: 0,
        failedTests: 0,
        totalDuration: 0,
        testDurations: []
      };
      return null;
    }
  });

  // Collect metrics after each test
  on('after:spec', (spec, results) => {
    results.tests.forEach(test => {
      const duration = test.attempts.reduce((sum, attempt) => sum + attempt.wallClockDuration, 0);
      cy.task('recordTestResult', {
        testName: test.title.join(' > '),
        status: test.state,
        duration: duration
      });
    });
  });

  // Generate final report after run
  on('after:run', (results) => {
    if (results) {
      return cy.task('generateMetricsReport');
    }
  });
};

