function logTestStep(stepName, details = {}) {
  const timestamp = new Date().toISOString();
  const logEntry = {
    timestamp,
    test: Cypress.currentTest.title,
    step: stepName,
    ...details
  };

  cy.task('log', JSON.stringify(logEntry));
  console.log(`[TEST STEP] ${stepName}:`, details);
}

