Cypress.Commands.add('analyzeFailure', () => {
  // Capture additional debugging information
  cy.url().then((url) => {
    cy.log(`Failure URL: ${url}`);
  });

  // Capture page HTML for analysis
  cy.document().then((doc) => {
    const html = doc.documentElement.outerHTML;
    // Save to file or send to logging service
    cy.task('saveFailureHtml', html);
  });

  // Capture browser console logs
  cy.window().then((win) => {
    cy.task('log', `Console logs at failure: ${JSON.stringify(win.consoleLogs || [])}`);
  });

  // Take detailed screenshot
  cy.screenshot(`failure-analysis-${Date.now()}`, {
    capture: 'fullPage',
    log: true
  });
});

