// 2. Intelligent Waiting Strategies
describe('Optimized Waiting Strategies', () => {
  it('uses intelligent waits instead of fixed delays', () => {
    cy.visit('/dashboard');

    // BAD: Fixed wait that always delays execution
    // cy.wait(2000);

    // GOOD: Wait for specific condition
    cy.get('[data-cy=loading-spinner]').should('not.exist');
    cy.get('[data-cy=content]').should('be.visible');

    // GOOD: Custom wait with timeout
    cy.get('[data-cy=async-content]', { timeout: 10000 }).should('be.visible');

    // GOOD: Wait for network request completion
    cy.intercept('/api/user-data').as('userData');
    cy.wait('@userData').then((interception) => {
      // Process response immediately
      expect(interception.response.statusCode).to.eq(200);
    });
  });

  it('implements retry strategies for flaky operations', () => {
    // Custom retry command
    Cypress.Commands.add('retryClick', { prevSubject: 'element' }, (subject, maxRetries = 3) => {
      let retries = 0;

      const attemptClick = () => {
        cy.wrap(subject).click({ force: true }).then({ timeout: 5000 }, () => {
          // Check if click was successful
          return cy.get('body').then(($body) => {
            // Define success condition
            if ($body.find('[data-cy=modal]').length > 0) {
              return true; // Success
            } else if (retries < maxRetries) {
              retries++;
              // Retry after short delay
              cy.wait(500);
              return attemptClick();
            } else {
              throw new Error(`Click failed after ${maxRetries} attempts`);
            }
          });
        });
      };

      return attemptClick();
    });

    // Usage
    cy.get('[data-cy=flaky-button]').retryClick();
    cy.get('[data-cy=modal]').should('be.visible');
  });
});

