// Button Component Implementation
// src/components/Button/Button.js
import React from 'react';

const Button = ({
  children,
  variant = 'primary',
  size = 'md',
  disabled = false,
  fullWidth = false,
  icon,
  iconPosition = 'left',
  onClick,
  className = '',
  ...props
}) => {
  // Base button classes
  const baseClasses = [
    'inline-flex',
    'items-center',
    'justify-center',
    'font-medium',
    'rounded-md',
    'transition-colors',
    'focus:outline-none',
    'focus:ring-2',
    'focus:ring-offset-2',
    'whitespace-nowrap'
  ];

  // Variant classes
  const variantClasses = {
    primary: [
      'bg-primary-600',
      'text-white',
      'hover:bg-primary-700',
      'focus:ring-primary-500',
      'border-transparent',
      disabled ? 'bg-primary-400' : ''
    ],
    secondary: [
      'bg-secondary-600',
      'text-white',
      'hover:bg-secondary-700',
      'focus:ring-secondary-500',
      'border-transparent',
      disabled ? 'bg-secondary-400' : ''
    ],
    outline: [
      'bg-transparent',
      'border',
      'border-primary-600',
      'text-primary-600',
      'hover:bg-primary-50',
      'focus:ring-primary-500',
      disabled ? 'border-primary-300 text-primary-300' : ''
    ],
    ghost: [
      'bg-transparent',
      'text-primary-600',
      'hover:bg-primary-50',
      'focus:ring-primary-500',
      'border-transparent'
    ]
  };

  // Size classes
  const sizeClasses = {
    sm: ['text-xs', 'py-1.5', 'px-3'],
    md: ['text-sm', 'py-2', 'px-4'],
    lg: ['text-base', 'py-2.5', 'px-5']
  };

  // State classes
  const stateClasses = [
    disabled ? 'cursor-not-allowed' : 'cursor-pointer'
  ];

  // Layout classes
  const layoutClasses = [
    fullWidth ? 'w-full' : ''
  ];

  // Combine all classes
  const classes = [
    ...baseClasses,
    ...variantClasses[variant],
    ...sizeClasses[size],
    ...stateClasses,
    ...layoutClasses,
    className
  ].filter(Boolean).join(' ');

  // Handle icon rendering
  const renderIcon = () => icon && (
    <span className={children ? (iconPosition === 'left' ? 'mr-2' : 'ml-2') : ''}>
      {icon}
    </span>
  );

  return (
    <button
      className={classes}
      disabled={disabled}
      onClick={onClick}
      {...props}
    >
      {iconPosition === 'left' && renderIcon()}
      {children}
      {iconPosition === 'right' && renderIcon()}
    </button>
  );
};

export default Button;

