/* Modern CSS3 Layout and Styling */

/* CSS Reset and Base Styles */
* {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
}

body {
    font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;
    line-height: 1.6;
    color: #333;
}

/* Flexbox Layout for Navigation */
.navbar {
    display: flex;
    justify-content: space-between;
    align-items: center;
    padding: 1rem 2rem;
    background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
    color: white;
}

.nav-links {
    display: flex;
    list-style: none;
    gap: 2rem;
}

.nav-links a {
    color: white;
    text-decoration: none;
    transition: opacity 0.3s ease;
}

.nav-links a:hover {
    opacity: 0.8;
}

/* CSS Grid Layout for Main Content */
.container {
    display: grid;
    grid-template-columns: 1fr 300px;
    grid-template-rows: auto 1fr auto;
    grid-template-areas:
        'header header'
        'main sidebar'
        'footer footer';
    min-height: 100vh;
    gap: 2rem;
    padding: 2rem;
}

.header { grid-area: header; }
.main { grid-area: main; }
.sidebar { grid-area: sidebar; }
.footer { grid-area: footer; }

/* Card Component with Modern CSS */
.card {
    background: white;
    border-radius: 12px;
    box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
    padding: 2rem;
    transition: transform 0.3s ease, box-shadow 0.3s ease;
}

.card:hover {
    transform: translateY(-5px);
    box-shadow: 0 8px 25px rgba(0, 0, 0, 0.15);
}

/* CSS3 Animations */
@keyframes fadeInUp {
    from {
        opacity: 0;
        transform: translateY(30px);
    }
    to {
        opacity: 1;
        transform: translateY(0);
    }
}

.animate-in {
    animation: fadeInUp 0.6s ease forwards;
}

/* Responsive Design with Media Queries */
@media (max-width: 768px) {
    .container {
        grid-template-columns: 1fr;
        grid-template-areas:
            'header'
            'main'
            'sidebar'
            'footer';
    }

    .navbar {
        flex-direction: column;
        gap: 1rem;
    }

    .nav-links {
        flex-direction: column;
        text-align: center;
        gap: 1rem;
    }
}

/* Modern CSS Features */
.button {
    background: linear-gradient(45deg, #667eea, #764ba2);
    color: white;
    border: none;
    padding: 1rem 2rem;
    border-radius: 8px;
    cursor: pointer;
    font-weight: 600;
    transition: all 0.3s ease;
    position: relative;
    overflow: hidden;
}

.button::before {
    content: '';
    position: absolute;
    top: 0;
    left: -100%;
    width: 100%;
    height: 100%;
    background: rgba(255, 255, 255, 0.2);
    transition: left 0.5s ease;
}

.button:hover::before {
    left: 100%;
}

// Sass (SCSS syntax) example demonstrating key preprocessor features

// 1. Variables for consistent theming
$primary-color: #007bff;
$secondary-color: #6c757d;
$success-color: #28a745;
$font-family-base: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;
$font-size-base: 16px;
$border-radius: 4px;

// 2. Nesting to mirror HTML structure
.navbar {
  background-color: $primary-color;
  padding: 1rem;

  // Nested selectors
  .nav-menu {
    display: flex;
    list-style: none;

    li {
      margin-right: 1rem;

      &:last-child {
        margin-right: 0;
      }

      a {
        color: white;
        text-decoration: none;
        padding: 0.5rem 1rem;
        border-radius: $border-radius;
        transition: background-color 0.3s ease;

        &:hover {
          background-color: darken($primary-color, 10%);
        }

        &.active {
          background-color: darken($primary-color, 15%);
          font-weight: bold;
        }
      }
    }
  }

  // Parent selector reference
  &--transparent {
    background-color: transparent;

    .nav-menu a {
      color: $secondary-color;

      &:hover {
        background-color: rgba($secondary-color, 0.1);
      }
    }
  }
}

// 3. Mixins for reusable code blocks
@mixin button-base {
  display: inline-block;
  padding: 0.5rem 1rem;
  border: none;
  border-radius: $border-radius;
  cursor: pointer;
  text-decoration: none;
  text-align: center;
  transition: all 0.3s ease;
  font-family: $font-family-base;
  font-size: $font-size-base;
}

@mixin button-variant($bg-color, $text-color: white) {
  @include button-base;
  background-color: $bg-color;
  color: $text-color;

  &:hover {
    background-color: darken($bg-color, 10%);
  }

  &:focus {
    outline: 2px solid lighten($bg-color, 20%);
    outline-offset: 2px;
  }

  &:disabled {
    background-color: lighten($bg-color, 20%);
    cursor: not-allowed;
    opacity: 0.6;
  }
}

// 4. Using mixins
.btn {
  @include button-base;

  &--primary {
    @include button-variant($primary-color);
  }

  &--secondary {
    @include button-variant($secondary-color);
  }

  &--success {
    @include button-variant($success-color);
  }

  &--large {
    padding: 0.75rem 1.5rem;
    font-size: 1.1rem;
  }

  &--small {
    padding: 0.25rem 0.5rem;
    font-size: 0.875rem;
  }
}

// 5. Functions for calculations and logic
@function calculate-rem($px-value) {
  @return ($px-value / $font-size-base) * 1rem;
}

@function tint($color, $percentage) {
  @return mix(white, $color, $percentage);
}

@function shade($color, $percentage) {
  @return mix(black, $color, $percentage);
}

// 6. Using functions
.responsive-text {
  font-size: calculate-rem(18px); // Compiles to 1.125rem
  padding: calculate-rem(20px) calculate-rem(30px); // Compiles to 1.25rem 1.875rem
}

// 7. Loops for generating repetitive styles
$sizes: 10, 20, 30, 40, 50;

@each $size in $sizes {
  .margin-#{$size} {
    margin: #{$size}px;
  }

  .padding-#{$size} {
    padding: #{$size}px;
  }
}

// 8. Conditionals for responsive design
$breakpoints: (
  small: 576px,
  medium: 768px,
  large: 992px,
  xlarge: 1200px
);

@mixin respond-to($breakpoint) {
  @if map-has-key($breakpoints, $breakpoint) {
    @media (min-width: map-get($breakpoints, $breakpoint)) {
      @content;
    }
  } @else {
    @warn "Unfortunately, no value could be retrieved from `#{$breakpoint}`. "
         + "Available breakpoints are: #{map-keys($breakpoints)}.";
  }
}

// 9. Using conditionals
.hero {
  padding: 2rem;
  background-color: $primary-color;
  color: white;
  text-align: center;

  @include respond-to(small) {
    padding: 3rem;
  }

  @include respond-to(medium) {
    padding: 4rem;
    font-size: 1.2rem;
  }

  @include respond-to(large) {
    padding: 5rem;
    font-size: 1.5rem;
  }
}

// 10. Extending classes for shared styles
%message-base {
  padding: 1rem;
  border-radius: $border-radius;
  margin-bottom: 1rem;
}

.message--info {
  @extend %message-base;
  background-color: tint($primary-color, 80%);
  border: 1px solid tint($primary-color, 50%);
  color: shade($primary-color, 20%);
}

.message--success {
  @extend %message-base;
  background-color: tint($success-color, 80%);
  border: 1px solid tint($success-color, 50%);
  color: shade($success-color, 20%);
}

.message--error {
  @extend %message-base;
  background-color: tint(#dc3545, 80%);
  border: 1px solid tint(#dc3545, 50%);
  color: shade(#dc3545, 20%);
}
